import { useParams } from 'react-router-dom';
import { useEffect, useState } from 'react';
import axios from 'axios';

function ProductDetailPage() {
  const { id } = useParams();
  const [product, setProduct] = useState(null);

  useEffect(() => {
    (async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/products/${id}`);
        setProduct(res.data);
      } catch {
        setProduct(null);
      }
    })();
  }, [id]);

  const handleAddToCart = () => {
    let cart = JSON.parse(localStorage.getItem('cart')) || [];
    const index = cart.findIndex(item => item.productId === product._id);
    if (index >= 0) {
      cart[index].quantity += 1;
    } else {
      cart.push({ productId: product._id, name: product.name, price: product.price, quantity: 1 });
    }
    localStorage.setItem('cart', JSON.stringify(cart));
    alert('Đã thêm vào giỏ hàng!');
  };

  if (!product) return <p>Không tìm thấy sản phẩm!</p>;

  return (
    <div className="form-box" style={{ maxWidth: 500 }}>
      <h2>{product.name}</h2>
      <img src={product.imageUrl || 'https://via.placeholder.com/200'} width={200} style={{ marginBottom: 16 }} />
      <p><b>Giá:</b> {product.price?.toLocaleString()} đ</p>
      <p>{product.description}</p>
      <button onClick={handleAddToCart}>Thêm vào giỏ hàng</button>
    </div>
  );
}
export default ProductDetailPage;
