import { useEffect, useState } from 'react';
import axios from 'axios';

function AdminProducts() {
  const [products, setProducts] = useState([]);
  const [editing, setEditing] = useState(null); // null | product id
  const [form, setForm] = useState({ name: '', price: '', description: '', image: null });
  const [preview, setPreview] = useState(null);

  const token = localStorage.getItem('token');

  // Load danh sách sản phẩm
  const fetchProducts = async () => {
    const res = await axios.get('http://localhost:5000/api/products');
    setProducts(res.data.products || res.data); // tuỳ backend trả về
  };

  useEffect(() => { fetchProducts(); }, []);

  // Xử lý thay đổi form
  const handleChange = (e) => {
    if (e.target.name === 'image') {
      setForm({ ...form, image: e.target.files[0] });
      setPreview(URL.createObjectURL(e.target.files[0]));
    } else {
      setForm({ ...form, [e.target.name]: e.target.value });
    }
  };

  // Thêm mới sản phẩm
  const handleAdd = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('name', form.name);
    formData.append('price', form.price);
    formData.append('description', form.description);
    if (form.image) formData.append('image', form.image);

    await axios.post(
      'http://localhost:5000/api/products',
      formData,
      { headers: { Authorization: `Bearer ${token}`, 'Content-Type': 'multipart/form-data' } }
    );
    setForm({ name: '', price: '', description: '', image: null });
    setPreview(null);
    fetchProducts();
  };

  // Sửa sản phẩm
  const handleEdit = (product) => {
    setEditing(product._id);
    setForm({
      name: product.name,
      price: product.price,
      description: product.description || '',
      image: null // reset file input
    });
    setPreview(product.image ? `http://localhost:5000/${product.image.replace(/\\/g, '/')}` : null);
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('name', form.name);
    formData.append('price', form.price);
    formData.append('description', form.description);
    if (form.image) formData.append('image', form.image);

    await axios.put(
      `http://localhost:5000/api/products/${editing}`,
      formData,
      { headers: { Authorization: `Bearer ${token}`, 'Content-Type': 'multipart/form-data' } }
    );
    setEditing(null);
    setForm({ name: '', price: '', description: '', image: null });
    setPreview(null);
    fetchProducts();
  };

  // Xoá sản phẩm
  const handleDelete = async (id) => {
    if (!window.confirm("Xác nhận xoá sản phẩm này?")) return;
    await axios.delete(
      `http://localhost:5000/api/products/${id}`,
      { headers: { Authorization: `Bearer ${token}` } }
    );
    fetchProducts();
  };

  return (
    <div>
      <h2>Quản lý sản phẩm</h2>
      <form onSubmit={editing ? handleUpdate : handleAdd} encType="multipart/form-data">
        <input name="name" placeholder="Tên sản phẩm" value={form.name} onChange={handleChange} required />
        <input name="price" type="number" placeholder="Giá" value={form.price} onChange={handleChange} required />
        <input name="description" placeholder="Mô tả" value={form.description} onChange={handleChange} />
        <input name="image" type="file" accept="image/*" onChange={handleChange} />
        {preview && (
          <img src={preview} alt="preview" width={60} style={{ marginLeft: 8, verticalAlign: 'middle' }} />
        )}
        <button type="submit">{editing ? "Cập nhật" : "Thêm mới"}</button>
        {editing && <button type="button" onClick={() => { setEditing(null); setForm({ name: '', price: '', description: '', image: null }); setPreview(null); }}>Huỷ</button>}
      </form>
      <table border="1" cellPadding={8}>
        <thead>
          <tr>
            <th>Tên</th>
            <th>Giá</th>
            <th>Mô tả</th>
            <th>Ảnh</th>
            <th>Thao tác</th>
          </tr>
        </thead>
        <tbody>
          {products.map(prod => (
            <tr key={prod._id}>
              <td>{prod.name}</td>
              <td>{prod.price.toLocaleString()}</td>
              <td>{prod.description}</td>
              <td>
                {prod.image ? (
                  <img src={`http://localhost:5000/${prod.image.replace(/\\/g, '/')}`} width={50} alt={prod.name} />
                ) : (
                  <img src="https://via.placeholder.com/50" width={50} alt="noimg" />
                )}
              </td>
              <td>
                <button onClick={() => handleEdit(prod)}>Sửa</button>
                <button onClick={() => handleDelete(prod._id)}>Xoá</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default AdminProducts;
