import { useEffect, useState } from 'react';
import axios from 'axios';

function AdminProducts() {
  const [products, setProducts] = useState([]);
  const [editing, setEditing] = useState(null); // null | product id
  const [form, setForm] = useState({ name: '', price: '', description: '', imageUrl: '' });

  const token = localStorage.getItem('token');

  // Load danh sách sản phẩm
  const fetchProducts = async () => {
    const res = await axios.get('http://localhost:5000/api/products');
    setProducts(res.data.products || res.data); // tuỳ backend trả về
  };

  useEffect(() => { fetchProducts(); }, []);

  // Xử lý thay đổi form
  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  // Thêm mới sản phẩm
  const handleAdd = async (e) => {
    e.preventDefault();
    await axios.post(
      'http://localhost:5000/api/products',
      form,
      { headers: { Authorization: `Bearer ${token}` } }
    );
    setForm({ name: '', price: '', description: '', imageUrl: '' });
    fetchProducts();
  };

  // Sửa sản phẩm
  const handleEdit = (product) => {
    setEditing(product._id);
    setForm({
      name: product.name, price: product.price,
      description: product.description || '', imageUrl: product.imageUrl || ''
    });
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    await axios.put(
      `http://localhost:5000/api/products/${editing}`,
      form,
      { headers: { Authorization: `Bearer ${token}` } }
    );
    setEditing(null);
    setForm({ name: '', price: '', description: '', imageUrl: '' });
    fetchProducts();
  };

  // Xoá sản phẩm
  const handleDelete = async (id) => {
    if (!window.confirm("Xác nhận xoá sản phẩm này?")) return;
    await axios.delete(
      `http://localhost:5000/api/products/${id}`,
      { headers: { Authorization: `Bearer ${token}` } }
    );
    fetchProducts();
  };

  return (
    <div>
      <h2>Quản lý sản phẩm</h2>
      <form onSubmit={editing ? handleUpdate : handleAdd}>
        <input name="name" placeholder="Tên sản phẩm" value={form.name} onChange={handleChange} required />
        <input name="price" type="number" placeholder="Giá" value={form.price} onChange={handleChange} required />
        <input name="description" placeholder="Mô tả" value={form.description} onChange={handleChange} />
        <input name="imageUrl" placeholder="Link ảnh" value={form.imageUrl} onChange={handleChange} />
        <button type="submit">{editing ? "Cập nhật" : "Thêm mới"}</button>
        {editing && <button type="button" onClick={() => { setEditing(null); setForm({ name: '', price: '', description: '', imageUrl: '' }); }}>Huỷ</button>}
      </form>
      <table border="1" cellPadding={8}>
        <thead>
          <tr>
            <th>Tên</th>
            <th>Giá</th>
            <th>Mô tả</th>
            <th>Ảnh</th>
            <th>Thao tác</th>
          </tr>
        </thead>
        <tbody>
          {products.map(prod => (
            <tr key={prod._id}>
              <td>{prod.name}</td>
              <td>{prod.price.toLocaleString()}</td>
              <td>{prod.description}</td>
              <td><img src={prod.imageUrl || "https://via.placeholder.com/50"} width={50} /></td>
              <td>
                <button onClick={() => handleEdit(prod)}>Sửa</button>
                <button onClick={() => handleDelete(prod._id)}>Xoá</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default AdminProducts;
