import { useState, useEffect } from 'react';
import axios from 'axios';

function CartPage() {
  const [cart, setCart] = useState([]);

  useEffect(() => {
    // Lấy giỏ hàng từ localStorage khi load trang
    const cartData = JSON.parse(localStorage.getItem('cart')) || [];
    setCart(cartData);
  }, []);

  // Tính tổng tiền
  const total = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);

  // Hàm đặt hàng
  const handleOrder = async () => {
    if (cart.length === 0) {
      alert("Giỏ hàng rỗng!");
      return;
    }
    // Chuẩn bị body API theo backend của bạn
    const orderItems = cart.map(item => ({
      product: item.productId,
      quantity: item.quantity,
      price: item.price
    }));

    try {
      // Gọi API tạo đơn hàng (thường cần token user)
      const token = localStorage.getItem('token');
      await axios.post(
        'http://localhost:5000/api/orders',
        { items: orderItems },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert('Đặt hàng thành công!');
      localStorage.removeItem('cart');
      setCart([]);
    } catch (err) {
      alert('Đặt hàng thất bại!');
    }
  };

  return (
    <div>
      <h2>Giỏ hàng của bạn</h2>
      {cart.length === 0 ? (
        <p>Giỏ hàng rỗng.</p>
      ) : (
        <table border="1" cellPadding={8}>
          <thead>
            <tr>
              <th>Tên sản phẩm</th>
              <th>Giá</th>
              <th>Số lượng</th>
              <th>Tổng</th>
            </tr>
          </thead>
          <tbody>
            {cart.map(item => (
              <tr key={item.productId}>
                <td>{item.name}</td>
                <td>{item.price.toLocaleString()}</td>
                <td>{item.quantity}</td>
                <td>{(item.price * item.quantity).toLocaleString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
      <h3>Tổng tiền: {total.toLocaleString()} đ</h3>
      <button onClick={handleOrder} disabled={cart.length === 0}>
        Đặt hàng
      </button>
    </div>
  );
}

export default CartPage;
