                </tr>
              </thead>
              <tbody>
                {cart.map(item => (
                  <tr key={item.productId}>
                    <td>{item.name}</td>
                    <td>{item.price.toLocaleString()} ƒë</td>
                    <td>{item.quantity}</td>
