import { useEffect, useState } from 'react';
import { getAllProducts } from '../api/productApi';

function HomePage() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    (async () => {
      try {
        const data = await getAllProducts();
        // Nếu API trả về object có mảng products
        setProducts(Array.isArray(data) ? data : (data.products || []));
      } catch (err) {
        alert("Không lấy được danh sách sản phẩm!");
      }
      setLoading(false);
    })();
  }, []);
  

  if (loading) return <p>Đang tải sản phẩm...</p>;

  return (
    <div>
      <h2>Danh sách sản phẩm</h2>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '16px' }}>
        {products.map(product => (
          <div key={product._id} style={{ border: '1px solid #ccc', padding: 12, width: 220 }}>
            <h4>{product.name}</h4>
            <img src={product.imageUrl || 'https://via.placeholder.com/150'} alt={product.name} width={150} />
            <p>Giá: {product.price.toLocaleString()} đ</p>
            <button>Chi tiết</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default HomePage;
